function sortAssortmentAndAddPopup(){const e=document.querySelector(".sort"),t=document.querySelector(".sort__list");e.addEventListener("click",()=>{t.classList.toggle("open")})}document.addEventListener("DOMContentLoaded",()=>{document.querySelector("#all").classList.add("clicked");const e=document.querySelector(".types"),t=document.querySelectorAll(".types__item");e.addEventListener("click",e=>{if("LI"!==e.target.tagName)return;const s=e.target.id,c=e.target;c.classList.contains("clicked")||(t.forEach(e=>e.classList.remove("clicked")),c.classList.add("clicked"));document.querySelectorAll(".assortment__item").forEach(e=>{e.classList.contains(s)||"all"===s?e.style.display="":e.style.display="none"})})}),sortAssortmentAndAddPopup();