function sortAssortmentAndAddPopup(){const t=document.querySelector(".sort"),e=document.querySelector(".sort__list"),c=document.querySelectorAll(".sort__item"),o=document.querySelector(".sort__choosen");t.addEventListener("click",()=>{e.classList.toggle("open")}),c.forEach(t=>t.addEventListener("click",t=>{o.textContent=t.target.textContent}))}document.addEventListener("DOMContentLoaded",()=>{document.querySelector("#all").classList.add("clicked");const t=document.querySelector(".types"),e=document.querySelectorAll(".types__item");t.addEventListener("click",t=>{if("LI"!==t.target.tagName)return;const c=t.target.id,o=t.target;o.classList.contains("clicked")||(e.forEach(t=>t.classList.remove("clicked")),o.classList.add("clicked"));document.querySelectorAll(".assortment__item").forEach(t=>{t.classList.contains(c)||"all"===c?t.style.display="":t.style.display="none"})})}),sortAssortmentAndAddPopup();